import java.util.Scanner;public class Fibonacci {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        int[] dp = new int[n+1];        System.out.println(fibMemoization(n, dp));        System.out.println(fibTabulation(n, dp));    }    public static int fibMemoization(int n, int[] dp){        if(n<=1){            return n;        }        if(dp[n] != 0){            return dp[n];        }        return dp[n] = fibMemoization(n-1, dp) + fibMemoization(n-2, dp);    }    public static int fibTabulation(int n, int[] dp){        dp[0] = 0;        dp[1] = 1;        for (int i = 2; i <= n ; i++) {            dp[i] = dp[i-1] + dp[i-2];        }        return dp[n];    }}