public class MatrixChain {    public static void main(String[] args) {        int[] arr = { 1, 2, 3, 4, 3 };        System.out.println(minCost(arr, arr.length));    }    public static int minCost(int[] p, int n){        int[][] m = new int[n][n];        int i, j, k, L, cost;        for (i  = 0; i < n ; i++) {            m[i][i] = 0;        }        for (L = 2 ; L < n; L++){            for (i = 1; i < n - L + 1; i++) {                j = i + L - 1;                if(j == n){                    continue;                }                m[i][j] = Integer.MAX_VALUE;                for (k = i; k < j; k++){                    cost = m[i][k] + m[k+1][j] + p[i-1]*p[k]*p[j];                    if (cost < m[i][j]){                        m[i][j] = cost;                    }                }            }        }        return m[1][n-1];    }}