public class SumOfSubsets {    public static void main(String[] args) {        int[] set = { 3, 4, 5, 2 };        int targetSum = 7;        findSubsets(set, targetSum);    }    public static void findSubsets(int[] set, int targetSum){        boolean[] solution = new boolean[set.length];        backtrack(set, solution, targetSum, 0, 0);    }    public static void backtrack(int[] set, boolean[] solution, int targetSum, int sumSoFar, int currentIndex){        if(sumSoFar == targetSum){            System.out.print("{ ");            for (int i = 0; i < set.length; i++) {                if(solution[i]){                    System.out.print(set[i] + " ");                }            }            System.out.print("}");            System.out.println();        }else if(sumSoFar < targetSum){            for (int i = currentIndex; i < set.length; i++) {                solution[i] = true;                backtrack(set, solution, targetSum, (sumSoFar + set[i]), i + 1);                solution[i] = false;            }        }    }}